mapper.select:
    	 SELECT A.*, B.nickname  FROM crochet A, user B 
		WHERE A.USERID = B.USERID
		 AND A.CRO_ID = #{croId}

mapper.update:
    	UPDATE crochet
		   SET 	cro_title = #{croTitle}
        	  , cro_content = #{croContent}
              , cro_rate = #{croRate}
              , cro_uuid = #{croUuid}
              , cro_filename = #{croFilename}
	     WHERE cro_id=#{croId};
